package a01.defaultvalues.basic.concepts;

public class PrintDefaultValues {
	
	static byte b ;
	static short s ;
	static int i ;
	static long l ;
	
	static float f ;
	static double d ;
	
	static char c ;
	
	static boolean bool ;
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Default value of " + 
							"b : " + b + ", " + 
							"s : " + s + ", " +
							"i : " + i + ", " +
							"l : " + l + ", " +
							"f : " + f + ", " +
							"d : " + d + ", " +
							"c : " + c + ", " +
							"bool : " + bool);
	}

}
